import React, { useState, useEffect, useRef } from "react";
import "./Game.css";

export const Game = () => {
  const [isJumping, setIsJumping] = useState(false);
  const [isDucking, setIsDucking] = useState(false);
  const [score, setScore] = useState(0);
  const [obstaclePosition, setObstaclePosition] = useState(0);
  const [isBird, setIsBird] = useState(false);
  const characterRef = useRef(null);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === "ArrowUp" && !isJumping && !isDucking) {
        jump();
      } else if (e.key === "ArrowDown" && !isDucking && !isJumping) {
        duck();
      }
    };

    const handleSwipeStart = (e) => {
      e.changedTouches[0].startY = e.changedTouches[0].clientY;
    };

    const handleSwipeEnd = (e) => {
      const touch = e.changedTouches[0];
      const swipeDirection = touch.clientY - touch.startY;
      if (swipeDirection < -50 && !isJumping && !isDucking) {
        jump();
      } else if (swipeDirection > 50 && !isDucking && !isJumping) {
        duck();
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("touchstart", handleSwipeStart);
    window.addEventListener("touchend", handleSwipeEnd);

    const gameInterval = setInterval(() => {
      setObstaclePosition((prev) => prev + 2);
      setScore((prev) => prev + 1);
      if (obstaclePosition > 100) resetObstacle();
      if (
        obstaclePosition < 5 &&
        obstaclePosition > 0 &&
        !isJumping &&
        !isDucking
      ) {
        if (isBird && !isDucking) {
          endGame();
        } else if (!isBird && !isJumping) {
          endGame();
        }
      }
    }, 50);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("touchstart", handleSwipeStart);
      window.removeEventListener("touchend", handleSwipeEnd);
      clearInterval(gameInterval);
    };
  }, [isJumping, isDucking, obstaclePosition, isBird]);

  const jump = () => {
    setIsJumping(true);
    setTimeout(() => {
      setIsJumping(false);
    }, 500);
  };

  const duck = () => {
    setIsDucking(true);
    setTimeout(() => {
      setIsDucking(false);
    }, 500);
  };

  const resetObstacle = () => {
    setIsBird(Math.random() < 0.5);
    return 100;
  };

  const endGame = () => {
    alert("Game Over!");
    setScore(0);
    setObstaclePosition(100);
    setIsBird(false);
  };

  return (
    <div className="game-container">
      <div className="score">Score: {score}</div>
      <div
        ref={characterRef}
        className={`character ${isJumping ? "jump" : ""} ${
          isDucking ? "duck" : ""
        }`}
      />
      <div
        className={`obstacle ${isBird ? "bird" : ""}`}
        style={{ right: `${obstaclePosition}%` }}
      />
    </div>
  );
};

export default Game;
