import React, { useState, useEffect, useRef } from "react";
import "./Game.css";

const Game = () => {
  const [isJumping, setIsJumping] = useState(false);
  const [isDucking, setIsDucking] = useState(false);
  const [score, setScore] = useState(0);
  const [obstaclePosition, setObstaclePosition] = useState(100);
  const [characterPosition, setCharacterPosition] = useState(0);
  const characterRef = useRef(null);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === "ArrowUp" && !isJumping) {
        jump();
      } else if (e.key === "ArrowDown" && !isDucking) {
        duck();
      }
    };

    const handleSwipe = (e) => {
      const touch = e.changedTouches[0];
      const swipeDirection = touch.clientY - touch.startY;
      if (swipeDirection < -50 && !isJumping) {
        jump();
      } else if (swipeDirection > 50 && !isDucking) {
        duck();
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener(
      "touchstart",
      (e) => (e.changedTouches[0].startY = e.changedTouches[0].clientY)
    );
    window.addEventListener("touchend", handleSwipe);

    const gameInterval = setInterval(() => {
      setObstaclePosition((prev) => (prev > 0 ? prev - 2 : 100));
      setScore((prev) => prev + 1);

      if (
        obstaclePosition < 5 &&
        obstaclePosition > 0 &&
        !isJumping &&
        !isDucking
      ) {
        alert("Game Over!");
        setScore(0);
        setObstaclePosition(100);
      }
    }, 50);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener(
        "touchstart",
        (e) => (e.changedTouches[0].startY = e.changedTouches[0].clientY)
      );
      window.removeEventListener("touchend", handleSwipe);
      clearInterval(gameInterval);
    };
  }, [isJumping, isDucking, obstaclePosition]);

  const jump = () => {
    setIsJumping(true);
    setTimeout(() => {
      setIsJumping(false);
    }, 500);
  };

  const duck = () => {
    setIsDucking(true);
    setTimeout(() => {
      setIsDucking(false);
    }, 500);
  };

  return (
    <div className="game-container">
      <div className="score">Score: {score}</div>
      <div
        ref={characterRef}
        className={`character ${isJumping ? "jump" : ""} ${
          isDucking ? "duck" : ""
        }`}
        style={{ bottom: characterPosition }}
      />
      <div className="obstacle" style={{ right: `${obstaclePosition}%` }} />
    </div>
  );
};

export default Game;
